.search-bar {
  padding: 15px calc(20% + 40px);
  background-color: #353535;
  display: flex;
  justify-content: space-between;
  z-index: 10000;
}

.search-bar__input {
  background-color: #272727;
  border: 0;
  padding: 15px;
  border-radius: 3px;
  color: #e5e5e5;
  flex: 2;
}

.search-bar__btn {
  width: 145px;
  padding: 15px;
  box-sizing: border-box;
  font-size: 1rem;
  color: #e5e5e5;
  background-color: #ab152a;
  border: 0;
  border-radius: 3px;
  cursor: pointer;
}

.search-bar__sort {
  background-color: #272727;
  color: #e5e5e5;
  width: 145px;
  padding: 15px;
  text-align: center;
  box-sizing: border-box;
  margin: 0 15px;
  border-radius: 3px;
  position: relative;
  cursor: pointer;
}
.search-bar__sort-content {
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.search-bar__sort-dropdown {
  position: absolute;
  width: 100%;
  text-align: center;
  display: block;
  box-sizing: border-box;
  left: 0;
  top: 49px;
}
.search-bar__sort-btn {
  background-color: #272727;
  color: #e5e5e5;
  width: 145px;
  padding: 15px;
  text-align: center;
  box-sizing: border-box;
  border-radius: 3px;
  cursor: pointer;
}
.search-bar__sort-btn:hover {
  color: #ab152a;
  background-color: #4c4c4c;
}

.search-bar__sort:hover {
  color: #ab152a;
  background-color: #262626;
}

.sort-arrow {
  margin-left: 5px;
}
